from web3 import Web3
from web3.middleware import geth_poa_middleware
import time

# Инициализация Web3
w3 = Web3(Web3.HTTPProvider('https://nova.arbitrum.io/rpc'))
w3.middleware_onion.inject(geth_poa_middleware, layer=0)

# Параметры
with open('Aprove/keys.txt', 'r') as file:
    private_keys = [line.strip() for line in file]  # Reads every line as a private key

token_contract_address = '0x750ba8b76187092B0D1E87E28daaf484d1b5273b' 

#  USDC - 0x750ba8b76187092b0d1e87e28daaf484d1b5273b 
# DAI - 0xDA10009cBd5D07dd0CeCc66161FC93D7c9000da1
# WETH - 0x722E8BdD2ce80A4422E880164f2079488e115365

dex_router_contract_address = '0x28e0f3ebab59a998C4f1019358388B5E2ca92cfA' 

#0x1b02dA8Cb0d097eB8D57A175b88c7D8b47997506 - Sushi,
#0x28e0f3ebab59a998C4f1019358388B5E2ca92cfA - RCP,
#0xEe01c0CD76354C383B8c7B4e65EA88D00B06f36f - arbswap

# Инициализация контракта   
