# Определение количества токенов для одобрения (максимально возможное количество)
amount_to_approve = 115792089237316195423570985008687907853269984665640564039457584007913129639935  # MAX_UINT256

for private_key in private_keys:
    account = w3.eth.account.from_key(private_key)

    # Подготовка транзакции
    txn_dict = {
        'from': account.address,
        'to': token_contract_address,
        'value': 0,
        'gas': 214580,
        'gasPrice': w3.toWei('0.01', 'gwei'),
        'nonce': w3.eth.get_transaction_count(account.address),
        'chainId': 42170,  # This is the chainId for Arbitrum Nova
        'data': token_contract.functions.approve(dex_router_contract_address, amount_to_approve).build_transaction({'from': account.address})['data']
    }

    # Подпись транзакции
    signed_txn = w3.eth.account.sign_transaction(txn_dict, private_key)

    # Отправка транзакции
    txn_hash = w3.eth.send_raw_transaction(signed_txn.rawTransaction)
    print('Transaction sent with hash:', txn_hash.hex())  # Prints the transaction hash

    txn_receipt = None
    while txn_receipt is None:
        time.sleep(5)  # Wait for 5 seconds before trying again
        txn_receipt = w3.eth.get_transaction_receipt(txn_hash)

        print('Transaction for', account.address, 'was mined in block', txn_receipt['blockNumber'])


