# Replace these variables with the tokens you want to swap
token_in_address = "0x750ba8b76187092B0D1E87E28daaf484d1b5273b"
token_out_address = "0xDA10009cBd5D07dd0CeCc66161FC93D7c9000da1"
token_in_amount = 0.00000000000001 #0.01 = 0.01 USDC
min_token_out_amount = 0.00000000000001 #0.000000000000001 = 0.01 DAI
path = [token_in_address, token_out_address]

# Load the Uniswap V2 Router contract
uniswap_v2_router = w3.eth.contract(address=uniswap_v2_router_address, abi=uniswap_v2_router_abi)

def send_transaction(from_wallet_private_key):
    # Get the from_wallet_address from the private key
    from_wallet_address = w3.eth.account.from_key(from_wallet_private_key).address

    deadline = w3.eth.get_block("latest")["timestamp"] + 300

    # Build the transaction
    transaction = uniswap_v2_router.functions.swapExactTokensForTokens(
        w3.to_wei(token_in_amount, "ether"),
        w3.to_wei(min_token_out_amount, "ether"),
        path,
        from_wallet_address,
        deadline
    ).build_transaction({
        'type': 2,  # EIP-1559 transaction
        'from': from_wallet_address,
        'gas': 247180,  # You can estimate the gas limit using w3.eth.estimateGas() method
        'maxFeePerGas': w3.to_wei('0.01', 'gwei'),
        'maxPriorityFeePerGas': w3.to_wei('0.01', 'gwei'),
        'nonce': w3.eth.get_transaction_count(from_wallet_address),
    })

    # Sign and send the transaction
    signed_transaction = w3.eth.account.sign_transaction(transaction, from_wallet_private_key)
    transaction_hash = w3.eth.send_raw_transaction(signed_transaction.rawTransaction)
    print(f"\nTransaction [{transaction_counter}] sent with hash: {transaction_hash.hex()}")

    receipt = w3.eth.wait_for_transaction_receipt(transaction_hash)

    if receipt['status'] == 1:
        print("Transaction successful!")
    else:
        print("Transaction failed!")

def print_sleep_bar(seconds):
    for _ in range(seconds):
        sys.stdout.write("-")
        sys.stdout.flush()
        time.sleep(1)

# Run the send_transaction function every 10 minutes, stopping after 30 transactions per wallet
transaction_counter = 0
while transaction_counter < 100:
    # Choose a random private key for each transaction
        wallet_private_key = random.choice(wallet_private_keys)
        send_transaction(wallet_private_key)
        pause_time = random.randint(1, 30)
        print(f"Waiting for {pause_time} seconds: [", end="")
        print_sleep_bar(pause_time)
        print("]")
        transaction_counter += 1
        
time.sleep(3)  # 10 minutes in seconds
